import {useState, useEffect} from 'react'
import axios from 'axios'

const CountriesList = ({countries}) => countries.map(country => <tr><td>{country.name}</td></tr>)

function App() {

  const [countries, setCountries] = useState([])
  const [searchCountry, setSearchCountry] = useState('')


  useEffect(() => {
    console.log('current input', searchCountry)

    // skip if currency is not defined
    if (searchCountry) {
      console.log('fetching countries...')
      axios
        .get(`https://restcountries.com/v3.1/name/${searchCountry}`)
        .then(response => {
          setCountries(response.data)
        })
    }
  }, [searchCountry])

  const onCountryChange = (event) => {
    setSearchCountry(event.target.value)
  }

  return (
    <div>
      countries: <input value={searchCountry} onChange={onCountryChange} />
      <table>
        <CountriesList countries={countries} />
      </table>
    </div>
  )
}

export default App;
