import {useState, useEffect} from 'react'
import axios from 'axios'

const CountriesList = ({countries}) => countries.map(country => <tr key={country.cca3}><td>{country.name.common}</td></tr>)

const Language = ({language}) => <li>{language}</li>

const CountryDetail = ({country}) => {

  if(country === null) {
    return null
  }

  return (
    <div>
      <h1><p>{country.name}</p></h1>
      capital {country.capital} <br/>
      area {country.area} <br/>

      <h2><p>languages:</p></h2>
      <ul><Language language={Object.keys(country.languages).map(key => country.languages[key])}/></ul>
    </div>

  )
}

const Notification = ({ message }) => {

  if (message === null) {
    return null
  }

  return (
    <div>
      {message}
    </div>
  )

}

function App() {

  const [countries, setCountries] = useState([])
  const [searchCountry, setSearchCountry] = useState('')
  const [countryDetail, setCountryDetail] = useState(null)
  const [message, setMessage] = useState(null)


  useEffect(() => {
    console.log('current input', searchCountry)

    // skip if currency is not defined
    if (searchCountry) {
      console.log('fetching countries...')
      axios
        .get(`https://restcountries.com/v3.1/name/${searchCountry}`)
        .then(response => {
          if(response.data.length > 10){
            setMessage('too many matches, specify another filter')
            setCountries([])
            setCountryDetail(null)
          }
          else if(response.data.length === 1){
            setMessage(null)
            setCountries([])
            setCountryDetail(response.data.filter(data => data))
          }
          else{
            setMessage(null)
            setCountries(response.data)
            setCountryDetail(null)
          }
        })
    }
    else{
      setMessage(null)
      setCountries([])
      setCountryDetail(null)
    }
  }, [searchCountry])

  const onCountryChange = (event) => {
    setSearchCountry(event.target.value)
  }

  return (
    <div>
      countries: <input value={searchCountry} onChange={onCountryChange} /> <br />
      <Notification message={message} />
      <CountryDetail country={countryDetail} />
      <table>
        <tbody>
        <CountriesList countries={countries} />
        </tbody>
      </table>
    </div>
  )
}

export default App;
