import {useState, useEffect} from 'react'
import axios from 'axios'
import CountryDetail from './Components/CountryDetail'
import Notification from './Components/Notification'
import Loading from './Components/Loading'

const CountriesList = ({countries, setCountryFunc}) => countries.map(country => <tr key={country.cca3}><td>{country.name.common}</td><td><button onClick={() => setCountryFunc(country)}>show</button></td></tr>)

function App() {

  const [allCountries, setAllCountries] = useState([])
  const [countries, setCountries] = useState([])
  const [searchCountry, setSearchCountry] = useState('')
  const [countryDetail, setCountryDetail] = useState(null)
  const [weatherData, setWeatherData] = useState(null)
  const [message, setMessage] = useState(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    axios
        .get(`https://restcountries.com/v3.1/all`)
        .then(response => { 
          setAllCountries (response.data)
          setLoading(false)
         } )
  }, [])

  useEffect(() => {
    const api_key = process.env.REACT_APP_API_KEY
    if(countryDetail){
      axios
          .get(`https://api.openweathermap.org/data/2.5/weather?lat=${countryDetail.capitalInfo.latlng[0]}&lon=${countryDetail.capitalInfo.latlng[0]}&appid=${api_key}&units=metric`)
          .then(response => { setWeatherData (response.data) } )
    }
    else{
      setWeatherData(null)
    }
  }, [countryDetail])

  const setOverallState = ({message, countries, countryDetail}) => {
    setMessage(message)
    setCountries(countries)
    setCountryDetail(countryDetail)
  }

  const onCountryChange = (event) => {
    const searchCountry = event.target.value
    const showCountries = allCountries.filter( country => country.name.common.toUpperCase().search(searchCountry.toUpperCase()) === -1 ? false : true )
    setSearchCountry(event.target.value)

    if(searchCountry === ''){
      setOverallState(null,[],null)
      //setMessage(null)
      //setCountries([])
      //setCountryDetail(null)
    }
    else{
      if(showCountries.length === 0){
        setMessage(null)
        setCountries([])
        setCountryDetail(null)
      }
      else if(showCountries.length > 10){
        setMessage('too many matches, specify another filter')
        setCountries([])
        setCountryDetail(null)
      }
      else if(showCountries.length === 1){
        setMessage(null)
        setCountries([])
        setCountryDetail(showCountries.reduce((prev, curr) => curr, {} ))
      }
      else{
        setMessage(null)
        setCountries(showCountries)
        setCountryDetail(null)   
      }
    }

  }

  const handleSetCountry = (country) => {
    setCountryDetail(country)
    setCountries([])
    setMessage(null)
  }

  if(loading){
    return (
      <Loading />
    )
  }

  return (
    <div>
      find countries <input value={searchCountry} onChange={onCountryChange} /> <br />
      <Notification message={message} />
      <CountryDetail country={countryDetail} weatherData={weatherData} />
      <table>
        <tbody>
          <CountriesList countries={countries} setCountryFunc={handleSetCountry}/>
        </tbody>
      </table>
    </div>
  )
}

export default App;
