import {useState, useEffect} from 'react'
import axios from 'axios'

const CountriesList = ({countries, setCountryFunc}) => countries.map(country => <tr key={country.cca3}><td>{country.name.common}</td><td><button onClick={() => setCountryFunc(country)}>show</button></td></tr>)

const CountryDetail = ({country}) => {
  if(country === null) {
    return null
  }

  return (
    <div>
      <h1><p>{country.name.common}</p></h1>
      capital {country.capital} <br/>
      area {country.area} <br/>

      <h2><p>languages:</p></h2>
      <ul>
      {Object.keys(country.languages).map(key => <li key={key}>{country.languages[key]}</li>)}
      </ul>
      <img src={country.flags.png}/>
    </div>

  )
}

const Notification = ({ message }) => {

  if (message === null) {
    return null
  }

  return (
    <div>
      {message}
    </div>
  )

}

function App() {

  const [countries, setCountries] = useState([])
  const [searchCountry, setSearchCountry] = useState('')
  const [countryDetail, setCountryDetail] = useState(null)
  const [message, setMessage] = useState(null)


  useEffect(() => {
    const ssSearchCountry  = searchCountry.toString()
    // skip if currency is not defined
    if (searchCountry) {

      axios
        .get(`https://restcountries.com/v3.1/name/${ssSearchCountry}`)
        .then(response => {
            console.log(ssSearchCountry)
            if(response.data.length > 10){
              console.log('>10')
              setMessage('too many matches, specify another filter')
              setCountries([])
              setCountryDetail(null)
            }
            else{
              console.log('<=10')
              setMessage(null)
              if(response.data.length === 1){
                console.log('=1')
                setCountries([])
                setCountryDetail(response.data.reduce((prev, curr) => curr, {} ))
                //setTimeout(setCountryDetail(response.data.reduce((prev, curr) => curr, {} )), 1000)
              }
              else{
                console.log('>1 <=10')
                //setTimeout(setCountries(response.data), 1000)
                setCountryDetail(null)
                setCountries(response.data)
              }

            }
        })
        .catch(response => {
          console.log('error')
          setMessage(null)
          setCountries([])
          setCountryDetail(null)
        } 
        )

    }
    else{
      setMessage(null)
      setCountries([])
      setCountryDetail(null)
    }
  }, [searchCountry])

  const onCountryChange = (event) => {
    setSearchCountry(event.target.value)
  }

  const handleSetCountry = (country) => {
    setCountryDetail(country)
    setCountries([])
    setMessage(null)
  }

  return (
    <div>
      find countries <input value={searchCountry} onChange={onCountryChange} /> <br />
      <Notification message={message} />
      <CountryDetail country={countryDetail} />
      <table>
        <tbody>
          <CountriesList countries={countries} setCountryFunc={handleSetCountry}/>
        </tbody>
      </table>
    </div>
  )
}

export default App;
