const Course = ({ course }) => {
  return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total sum={course.parts.map(exercises => {exercises}).reduce((total, exercises) => total + exercises)} />
    </div>
  )
}

const Header = ({ course }) => <h1>{course}</h1>

const Total = ({ sum }) => <p>Number of exercises {sum}</p>

const Part = ({ part }) => 
  <p>
    {part.name} {part.exercises}
  </p>

const Content = ({ parts }) => {

  return(
    parts.map(part => <Part key={part.id} part={part} />)
  )

}

const App = () => {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'State of art',
        exercises: 18,
        id: 4
      },
      {
        name: 'State of heart',
        exercises: 33,
        id: 5
      },
      {
        name: 'State of machine',
        exercises: 22,
        id: 6
      }
    ]
  }

  return <Course course={course} />
}

export default App
